package com.infosys.electronicstore.baseEntity;import lombok.Data;import org.hibernate.annotations.CreationTimestamp;import org.hibernate.annotations.UpdateTimestamp;import javax.persistence.Column;import javax.persistence.MappedSuperclass;import java.time.LocalDate;@Data@MappedSuperclasspublic class DtoBaseEntity {        @Column(name = "created_date",updatable = false)        @CreationTimestamp        private LocalDate createdDate;        @Column(name = "updated_date",insertable = false)        @UpdateTimestamp        private LocalDate updatedDate;        @Column(name = "is_active_switch")        private String isActive;    }