package com.infosys.electronicstore.dtos;import com.infosys.electronicstore.baseEntity.DtoBaseEntity;import com.infosys.electronicstore.validate.ImageNameValid;import lombok.*;import javax.validation.constraints.*;@Getter@Setter@AllArgsConstructor@NoArgsConstructor@Builderpublic class UserDto extends DtoBaseEntity {    private long userId;    @NotBlank(message = "Please fill in Name")    @Size(min = 2, max = 50, message = "Enter Character Min = 2 & Max = 50")    private String name;    @NotBlank(message = "Please fill in Email")    @Email(message = "Please fill in valid Email")    private String email;    @NotEmpty(message = "Please fill in Password")    @Pattern(regexp = "^(?=.*[0-9])(?=.*[a-z])(?=.*[A-Z])(?=.*[@#$%^&+=])(?=\\S+$).{8,}$", message = "Please fill 1 Capital letter, 1 Small letter, ")    private String password;  /*  At least 8 chars    Contains at least one digit    Contains at least one lower alpha char and one upper alpha char    Contains at least one char within a set of special chars (@#%$^ etc.)    Does not contain space, tab, etc.    */    @NotBlank(message = "Please select Gender")    private String gender;    @NotBlank(message = "Please fill in About")    @Size(min = 5, max = 200, message = "Enter Character Min = 2 & Max = 50")    private String about;    @ImageNameValid    private String imageName;}