package com.infosys.electronicstore.entities;import com.infosys.electronicstore.baseEntity.BaseEntity;import lombok.*;import javax.persistence.*;import java.util.Date;@Entity@Table(name = "products")@Setter@Getter@AllArgsConstructor@NoArgsConstructorpublic class Product extends BaseEntity {    @Id    private String productId;    @Column(name = "product_title",length = 100)    private String title;    @Column(name = "product_description",length = 10000)    private String description;    @Column(name = "product_price")    private int price;    @Column(name = "product_discountedPrice")    private int discountedPrice;    private int quantity;    @Column(name = "product_addDate")    private Date addedDate;    private boolean live;    private boolean stock;}