package com.infosys.electronicstore.repositories;import com.infosys.electronicstore.entities.Product;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.data.jpa.repository.JpaRepository;public interface ProductRepository extends JpaRepository<Product,String> {    Page<Product> findByTitleContaining(String subtitle, Pageable pageable);    Page<Product> findByLiveTrue(Pageable pageable);}